import React, { useState } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';

const AuthenticationPage = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const { user, total } = location.state || {};

  const [method, setMethod] = useState(''); // Selected authentication method
  const [inputValue, setInputValue] = useState('');
  const [error, setError] = useState('');

  const handleAuthentication = () => {
    setError('');
    if (method === 'PIN') {
      if (inputValue === '1234') {
        navigate('/challan', { state: { user, total } }); // Replace `/challan` with the next page
      } else {
        setError('Invalid PIN. Please try again.');
      }
    } else if (method === 'OTP') {
      if (inputValue === '567890') {
        navigate('/challan', { state: { user, total } });
      } else {
        setError('Invalid OTP. Please try again.');
      }
    } else if (method === 'Biometric') {
      navigate('/challan', { state: { user, total } });
    } else {
      setError('Please select an authentication method.');
    }
  };

  return (
    <div>
      <h1>Authentication Page</h1>
      <p><strong>User:</strong> {user?.name || 'Unknown'}</p>
      <p><strong>Account Number:</strong> {user?.accountNumber || 'Unknown'}</p>
      <p><strong>Total Amount:</strong> â‚¹{total || '0'}</p>

      <h2>Select Authentication Method</h2>
      <div>
        <label>
          <input
            type="radio"
            name="auth-method"
            value="PIN"
            onChange={() => setMethod('PIN')}
          />
          PIN
        </label>
        <label>
          <input
            type="radio"
            name="auth-method"
            value="OTP"
            onChange={() => setMethod('OTP')}
          />
          OTP
        </label>
        <label>
          <input
            type="radio"
            name="auth-method"
            value="Biometric"
            onChange={() => setMethod('Biometric')}
          />
          Biometric
        </label>
      </div>

      {method && (
        <div>
          <h3>Enter {method}</h3>
          <input
            type={method === 'Biometric' ? 'button' : 'text'}
            placeholder={method === 'PIN' ? 'Enter PIN' : method === 'OTP' ? 'Enter OTP' : 'Simulating Biometric...'}
            value={method === 'Biometric' ? 'Simulate Biometric' : inputValue}
            onChange={(e) => setInputValue(e.target.value)}
            onClick={method === 'Biometric' ? handleAuthentication : undefined}
          />
        </div>
      )}

      {error && <p style={{ color: 'red' }}>{error}</p>}

      <button onClick={handleAuthentication}>Authenticate</button>
    </div>
  );
};

export default AuthenticationPage;
