// 
import React, { useEffect, useState } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import { useTranslation } from 'react-i18next';
import styles from './WithdrawalPage.module.css';

const WithdrawalPage = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const { t } = useTranslation();
  const accountNumber = location.state?.accountNumber; // Pass accountNumber from previous page
  const [user, setUser] = useState(null);
  const [withdrawalAmount, setWithdrawalAmount] = useState('');
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(true);

  // Fetch user details from the backend
  useEffect(() => {
    const fetchUserDetails = async () => {
      try {
        console.log(`Fetching user details for account number: ${accountNumber}`);
        const response = await fetch(`/api/user-details/${accountNumber}`);
        if (!response.ok) {
          throw new Error('Failed to fetch user details');
        }
        const data = await response.json();

        // Ensure balance is converted to a number
        if (data.balance) {
          data.balance = parseFloat(data.balance);
        }

        console.log('Fetched user details:', data);
        setUser(data);
      } catch (error) {
        console.error('Error fetching user details:', error.message);
        setUser(null);
        setError(t('errorFetchingUserDetails'));
      }
    };

    if (accountNumber) {
      fetchUserDetails();
    } else {
      setError(t('noAccountNumberProvided'));
    }
  }, [accountNumber, t]);

  const handleNext = () => {
    setError('');
    const accountBalance = user?.balance || 0;

    if (!withdrawalAmount || isNaN(withdrawalAmount)) {
      setError(t('enterValidAmount'));
      return;
    }

    if (withdrawalAmount > accountBalance) {
      setError(t('insufficientBalance'));
      return;
    }

    navigate('/authentication', { state: { user, total: withdrawalAmount } });
  };

  if (loading) {
    return <p className={styles.loading}>{t('loading')}</p>;
  }

  if (!user) {
    return <p className={styles.error}>{t('userNotFound')}</p>;
  }

  return (
    <div className={styles.container}>
      <div className={styles.card}>
        <h1 className={styles.title}>{t('withdrawalPageTitle')}</h1>
        <p className={styles.subtitle}>
          <strong>{t('accountHolder')}</strong> {user.name || t('unknown')}
        </p>
        <p className={styles.subtitle}>
          <strong>{t('accountNumberLabel')}</strong> {user.account_number || t('unknown')}
        </p>
        <p className={styles.subtitle}>
          <strong>{t('accountBalance')}</strong> â‚¹{parseFloat(user.balance).toFixed(2) || t('unknown')}
        </p>

        <div className={styles.inputContainer}>
          <label htmlFor="withdrawalAmount" className={styles.label}>
            {t('enterWithdrawalAmount')}
          </label>
          <input
            type="number"
            id="withdrawalAmount"
            className={styles.input}
            value={withdrawalAmount}
            onChange={(e) => setWithdrawalAmount(parseFloat(e.target.value))}
            placeholder={t('enterWithdrawalAmount')}
          />
        </div>

        {error && <p className={styles.error}>{error}</p>}

        <button onClick={handleNext} className={styles.button}>
          {t('nextButton')}
        </button>
      </div>
    </div>
  );
};

export default WithdrawalPage;
