import React from 'react';
import { useNavigate } from 'react-router-dom';
import OCRScanner from '../components/OCRScanner';

const HomePage = ({ onScanComplete }) => {
  const navigate = useNavigate();

  const handleScanComplete = (data) => {
    onScanComplete(data);
    navigate('/user-details', { state: { accountNumber: data.accountNumber, ifscCode: data.ifscCode } });
  };

  return (
    <div className="flex justify-center items-center min-h-screen bg-gray-100">
      <Card className="max-w-lg p-6">
        <h1 className="text-2xl font-semibold mb-4">OCR Scanner</h1>
        <p className="mb-6">Keep your passbook for scanning.</p>
        <div>
          <Button
            onClick={handleScan}
            className="w-full"
            disabled={loading}
          >
            {loading ? <Loader className="mr-2" /> : 'Start Scan'}
          </Button>
        </div>
      </Card>
    </div>
    
  );
};

export default HomePage;
