import React, { useState } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import { useTranslation } from 'react-i18next'; // Import translation hook
import styles from './AuthenticationPage.module.css';

const AuthenticationPage = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const { t } = useTranslation(); // Initialize translation hook
  const { user, total, denominations, type } = location.state || {}; // Get denominations and type from state

  const [method, setMethod] = useState('');
  const [inputValue, setInputValue] = useState('');
  const [error, setError] = useState('');

  const handleAuthentication = async () => {
    setError('');
    if (!user || !total) {
        setError('Transaction data is missing.');
        return;
    }

    // âœ… Normalize transaction type
    const formattedType = type.charAt(0).toUpperCase() + type.slice(1).toLowerCase(); // "deposit" â†’ "Deposit"

    if (method === 'PIN' && inputValue === '1234' ||
        method === 'OTP' && inputValue === '567890' ||
        method === 'Biometric') {

        console.log(`ðŸŸ¢ Authentication successful! Processing ${formattedType} of â‚¹${total}`);

        try {
            const response = await fetch('/api/process-transaction', {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({
                    accountNumber: user.account_number,
                    amount: total,
                    type: formattedType  // âœ… Ensures correct case for DB check constraint
                }),
            });

            const data = await response.json();
            console.log("ðŸŸ¢ Transaction processed:", data);

            navigate('/challan', { state: { user, total, type: formattedType, transaction: data.transaction } });

        } catch (error) {
            console.error("ðŸ”´ Transaction processing error:", error);
            setError('Transaction failed. Please try again.');
        }

    } else {
        setError(t('invalidAuth'));
    }
};




  return (
    <div className={styles.container}>
      <div className={styles.card}>
        <h1 className={styles.title}>{t('authenticationPageTitle')}</h1>
        <p className={styles.subtitle}>
          {t('transactionType')} {type === 'deposit' ? t('deposit') : t('withdrawal')}
        </p>
        <p className={styles.subtitle}>
          {t('userLabel')} {user?.name || t('unknown')}
        </p>
        <p className={styles.subtitle}>
          {t('accountNumberLabel')} {user?.account_number || t('unknown')}
        </p>
        <p className={styles.subtitle}>
          {t('totalAmountLabel')} â‚¹{total || 0}
        </p>

        <h2 className={styles.subtitle}>{t('selectAuthMethod')}</h2>
        <div className={styles.radioGroup}>
          <label>
            <input
              type="radio"
              name="auth-method"
              value="PIN"
              onChange={() => setMethod('PIN')}
            />
            PIN
          </label>
          <label>
            <input
              type="radio"
              name="auth-method"
              value="OTP"
              onChange={() => setMethod('OTP')}
            />
            OTP
          </label>
          <label>
            <input
              type="radio"
              name="auth-method"
              value="Biometric"
              onChange={() => setMethod('Biometric')}
            />
            Biometric
          </label>
        </div>

        {method && (
          <div className={styles.inputContainer}>
            <h3 className={styles.subtitle}>{t('enterMethod', { method })}</h3>
            <input
              type={method === 'Biometric' ? 'button' : 'text'}
              placeholder={
                method === 'PIN'
                  ? t('pinPlaceholder')
                  : method === 'OTP'
                    ? t('otpPlaceholder')
                    : t('simulateBiometric')
              }
              value={method === 'Biometric' ? t('simulateBiometric') : inputValue}
              onChange={(e) => setInputValue(e.target.value)}
              onClick={method === 'Biometric' ? handleAuthentication : undefined}
              className={styles.input}
            />
          </div>
        )}

        {error && <p className={styles.error}>{error}</p>}

        <button onClick={handleAuthentication} className={styles.button}>
          {t('authenticateButton')}
        </button>
      </div>
    </div>
  );
};

export default AuthenticationPage;
