import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import HomePage from './pages/HomePage';
import UserDetailsPage from './pages/UserDetailsPage';
import DepositPage from './pages/DepositPage';
import AuthenticationPage from './pages/AuthenticationPage';
import ChallanPage from './pages/ChallanPage';
import WithdrawalPage from './pages/WithdrawalPage';
import AdminDashboard from './pages/AdminDashboard';
import UserManagement from './pages/UserManagement';
import TransactionMonitor from './pages/TransactionMonitor';
import LoginPage from './pages/LoginPage'; // Import LoginPage
import ProtectedRoute from './components/ProtectedRoute';

function App() {
  const [ocrData, setOcrData] = useState(null);
  const [authToken, setAuthToken] = useState(localStorage.getItem('authToken'));

  return (
    <Router>
      <Routes>
        {/* Public Routes */}
        <Route path="/" element={<HomePage onScanComplete={setOcrData} />} />
        <Route path="/user-details" element={<UserDetailsPage />} />
        <Route path="/deposit" element={<DepositPage />} />
        <Route path="/authentication" element={<AuthenticationPage />} />
        <Route path="/challan" element={<ChallanPage />} />
        <Route path="/withdrawal" element={<WithdrawalPage />} />
        <Route path="/login" element={<LoginPage setAuthToken={setAuthToken} />} /> {/* Login Route */}

        {/* Protected Routes */}
        <Route
          path="/admin"
          element={
            <ProtectedRoute>
              <AdminDashboard />
            </ProtectedRoute>
          }
        />
        <Route
          path="/admin/users"
          element={
            <ProtectedRoute>
              <UserManagement />
            </ProtectedRoute>
          }
        />
        <Route
          path="/admin/transactions"
          element={
            <ProtectedRoute>
              <TransactionMonitor />
            </ProtectedRoute>
          }
        />
      </Routes>
    </Router>
  );
}

export default App;
